            cursor: pointer;
            text-align: center;
            position: relative;
            p {
                margin-top: 5px;
            }
        }
        .cardone::after {
            content: "";
            width: 0;
            height: 4px;
            position: absolute;
            left: 0;
            bottom: 0;
            background-color: #1e6bdd;
            transition: all 0.2s linear;
        }

        .cardone:hover::after {
            width: 100%;
        }
    }
}

